static error_t parse_arg (int key, char *arg, struct argp_state *state)
{
    long pid;
    switch (key) {
    case 'p' :
        errno = 0;
        pid = strtol (arg, NULL, 10);
        if (errno || pid <= 0) {
            warn ("Invalid PID: %s\n", arg);
            argp_usage (state);
        }
        target_pid = pid;
        break;
    case 't' :
        emit_timestamp = true;
        break;
    case 'x' :
        trace_failed_only = true;
        break;
    case 'T' :
        trace_by_process = false;
        break;
    case 'v' :
        verbose = true;
        break;
    case 'c' :
        env.cgroupspath = arg;
        env.cg = true;
        break;
    case 'h' :
        argp_state_help (state, stderr, ARGP_HELP_STD_HELP);
        break;
    default :
        return ARGP_ERR_UNKNOWN;
    }
    return 0;
}

static int libbpf_print_fn (enum libbpf_print_level level, const char *format, va_list args)
{
    if (level == LIBBPF_DEBUG && !verbose)
        return 0;
    return vfprintf (stderr, format, args);
}

static void handle_event (void *ctx, int cpu, void *data, __u32 data_sz)
{
    struct event *e = data;
    time_t t;
    struct tm *tm;
    char ts [32];
    double age;
    int sig, coredump;
    if (emit_timestamp) {
        time (&t);
        tm = localtime (& t);
        strftime (ts, sizeof (ts), "%H:%M:%S", tm);
        printf ("%8s ", ts);
    }
    age = (e->exit_time - e->start_time) / 1e9;
    printf ("%-16s %-7d %-7d %-7d %-7.2f ", e->comm, e->pid, e->ppid, e->tid, age);
    if (!e->sig) {
        if (!e->exit_code)
            printf ("0\n");
        else
            printf ("code %d\n", e->exit_code);
    }
    else {
        sig = e->sig & 0x7f;
        coredump = e->sig & 0x80;
        if (sig)
            printf ("signal %d (%s)", sig, strsignal (sig));
        if (coredump)
            printf (", core dumped");
        printf ("\n");
    }
}

static void handle_lost_events (void *ctx, int cpu, __u64 lost_cnt)
{
    warn ("lost %llu events on CPU #%d\n", lost_cnt, cpu);
}

int main (int argc, char **argv)
{
    LIBBPF_OPTS (bpf_object_open_opts, open_opts);
    static const struct argp argp = {
        .options = opts,
        .parser = parse_arg,
        .doc = argp_program_doc,}
    ;
    struct perf_buffer *pb = NULL;
    struct exitsnoop_bpf *obj;
    int err;
    int idx, cg_map_fd;
    int cgfd = -1;
    err = argp_parse (& argp, argc, argv, 0, NULL, NULL);
    if (err)
        return err;
    libbpf_set_strict_mode (LIBBPF_STRICT_ALL);
    libbpf_set_print (libbpf_print_fn);
    err = ensure_core_btf (& open_opts);
    if (err) {
        fprintf (stderr, "failed to fetch necessary BTF for CO-RE: %s\n", strerror (-err));
        return 1;
    }
    obj = exitsnoop_bpf__open_opts (& open_opts);
    if (!obj) {
        warn ("failed to open BPF object\n");
        return 1;
    }
    obj->rodata->target_pid = target_pid;
    obj->rodata->trace_failed_only = trace_failed_only;
    obj->rodata->trace_by_process = trace_by_process;
    obj->rodata->filter_cg = env.cg;
    err = exitsnoop_bpf__load (obj);
    if (err) {
        warn ("failed to load BPF object: %d\n", err);
        goto cleanup;
    }
    if (env.cg) {
        idx = 0;
        cg_map_fd = bpf_map__fd (obj -> maps.cgroup_map);
        cgfd = open (env.cgroupspath, O_RDONLY);
        if (cgfd < 0) {
            fprintf (stderr, "Failed opening Cgroup path: %s", env.cgroupspath);
            goto cleanup;
        }
        if (bpf_map_update_elem (cg_map_fd, &idx, &cgfd, BPF_ANY)) {
            fprintf (stderr, "Failed adding target cgroup to map");
            goto cleanup;
        }
    }
    err = exitsnoop_bpf__attach (obj);
    if (err) {
        warn ("failed to attach BPF programs: %d\n", err);
        goto cleanup;
    }
    pb = perf_buffer__new (bpf_map__fd (obj -> maps.events), PERF_BUFFER_PAGES, handle_event, handle_lost_events, NULL, NULL);
    if (!pb) {
        err = -errno;
        warn ("failed to open perf buffer: %d\n", err);
        goto cleanup;
    }
    if (signal (SIGINT, sig_int) == SIG_ERR) {
        warn ("can't set signal handler: %s\n", strerror (errno));
        err = 1;
        goto cleanup;
    }
    if (emit_timestamp)
        printf ("%-8s ", "TIME(s)");
    printf ("%-16s %-7s %-7s %-7s %-7s %-s\n", "PCOMM", "PID", "PPID", "TID", "AGE(s)", "EXIT_CODE");
    while (!exiting) {
        err = perf_buffer__poll (pb, PERF_POLL_TIMEOUT_MS);
        if (err < 0 && err != -EINTR) {
            warn ("error polling perf buffer: %s\n", strerror (-err));
            goto cleanup;
        }
        err = 0;
    }
cleanup :
    perf_buffer__free (pb);
    exitsnoop_bpf__destroy (obj);
    cleanup_core_btf (&open_opts);
    if (cgfd > 0)
        close (cgfd);
    return err != 0;
}
