static error_t parse_arg (int key, char *arg, struct argp_state *state)
{
    long n;
    switch (key) {
    case 'p' :
        errno = 0;
        n = strtol (arg, NULL, 10);
        if (errno || n <= 0) {
            fprintf (stderr, "Invalid PID: %s\n", arg);
            argp_usage (state);
        }
        target_pid = n;
        break;
    case '4' :
        target_family = AF_INET;
        break;
    case '6' :
        target_family = AF_INET6;
        break;
    case 'w' :
        column_width = 26;
        break;
    case 'L' :
        target_sports = strdup (arg);
        break;
    case 'D' :
        target_dports = strdup (arg);
        break;
    case 'T' :
        emit_timestamp = true;
        break;
    case 'v' :
        verbose = true;
        break;
    case 'h' :
        argp_state_help (state, stderr, ARGP_HELP_STD_HELP);
        break;
    default :
        return ARGP_ERR_UNKNOWN;
    }
    return 0;
}

static int libbpf_print_fn (enum libbpf_print_level level, const char *format, va_list args)
{
    if (level == LIBBPF_DEBUG && !verbose)
        return 0;
    return vfprintf (stderr, format, args);
}

static void handle_event (void *ctx, int cpu, void *data, __u32 data_sz)
{
    char ts [32], saddr [48], daddr [48];
    struct event *e = data;
    struct tm *tm;
    time_t t;
    if (emit_timestamp) {
        time (&t);
        tm = localtime (& t);
        strftime (ts, sizeof (ts), "%H:%M:%S", tm);
        printf ("%8s ", ts);
    }
    inet_ntop (e->family, &e->saddr, saddr, sizeof (saddr));
    inet_ntop (e->family, &e->daddr, daddr, sizeof (daddr));
    printf ("%-7d %-16s %-*s %-5d %-*s %-5d %-6.2f %-6.2f %-.2f\n", e->pid, e->comm, column_width, saddr, e->sport, column_width, daddr, e->dport, (double) e->tx_b / 1024, (double) e->rx_b / 1024, (double) e->span_us / 1000);
}

static void handle_lost_events (void *ctx, int cpu, __u64 lost_cnt)
{
    fprintf (stderr, "lost %llu events on CPU #%d\n", lost_cnt, cpu);
}

int main (int argc, char **argv)
{
    LIBBPF_OPTS (bpf_object_open_opts, open_opts);
    static const struct argp argp = {
        .options = opts,
        .parser = parse_arg,
        .doc = argp_program_doc,}
    ;
    struct tcplife_bpf *obj;
    struct perf_buffer *pb = NULL;
    short port_num;
    char *port;
    int err, i;
    err = argp_parse (& argp, argc, argv, 0, NULL, NULL);
    if (err)
        return err;
    libbpf_set_strict_mode (LIBBPF_STRICT_ALL);
    libbpf_set_print (libbpf_print_fn);
    err = ensure_core_btf (& open_opts);
    if (err) {
        fprintf (stderr, "failed to fetch necessary BTF for CO-RE: %s\n", strerror (-err));
        return 1;
    }
    obj = tcplife_bpf__open_opts (& open_opts);
    if (!obj) {
        fprintf (stderr, "failed to open BPF object\n");
        return 1;
    }
    obj->rodata->target_pid = target_pid;
    obj->rodata->target_family = target_family;
    if (target_sports) {
        i = 0;
        port = strtok (target_sports, ",");
        while (port && i < MAX_PORTS) {
            port_num = strtol (port, NULL, 10);
            obj->rodata->target_sports[i++] = port_num;
            port = strtok (NULL, ",");
        }
        obj->rodata->filter_sport = true;
    }
    if (target_dports) {
        i = 0;
        port = strtok (target_dports, ",");
        while (port && i < MAX_PORTS) {
            port_num = strtol (port, NULL, 10);
            obj->rodata->target_dports[i++] = port_num;
            port = strtok (NULL, ",");
        }
        obj->rodata->filter_dport = true;
    }
    err = tcplife_bpf__load (obj);
    if (err) {
        fprintf (stderr, "failed to load BPF object: %d\n", err);
        goto cleanup;
    }
    err = tcplife_bpf__attach (obj);
    if (err) {
        fprintf (stderr, "failed to attach BPF object: %d\n", err);
        goto cleanup;
    }
    pb = perf_buffer__new (bpf_map__fd (obj -> maps.events), PERF_BUFFER_PAGES, handle_event, handle_lost_events, NULL, NULL);
    if (!pb) {
        err = -errno;
        fprintf (stderr, "failed to open perf buffer: %d\n", err);
        goto cleanup;
    }
    if (signal (SIGINT, sig_int) == SIG_ERR) {
        fprintf (stderr, "can't set signal handler: %s\n", strerror (errno));
        err = 1;
        goto cleanup;
    }
    if (emit_timestamp)
        printf ("%-8s ", "TIME(s)");
    printf ("%-7s %-16s %-*s %-5s %-*s %-5s %-6s %-6s %-s\n", "PID", "COMM", column_width, "LADDR", "LPORT", column_width, "RADDR", "RPORT", "TX_KB", "RX_KB", "MS");
    while (!exiting) {
        err = perf_buffer__poll (pb, PERF_POLL_TIMEOUT_MS);
        if (err < 0 && err != -EINTR) {
            fprintf (stderr, "error polling perf buffer: %s\n", strerror (-err));
            goto cleanup;
        }
        err = 0;
    }
cleanup :
    perf_buffer__free (pb);
    tcplife_bpf__destroy (obj);
    cleanup_core_btf (&open_opts);
    return err != 0;
}
