int reallocate_invalidates (struct sk_buff *ctx)
{
    void *data_end = (void *) (long) ctx->data_end;
    void *data = (void *) (long) ctx->data;
    if (data + sizeof (int) > data_end)
        return 1;
    int value = *(int*) data;
    *(int*) data = value + 1;
    bpf_skb_change_head (ctx, 4, 0);
    value = *(int*) data;
    *(int*) data = value + 1;
    return 0;
}
